const state = {
    albums:[],
    artist:{
        name:'Tião Carreiro',
    },

    search:'',
    albumMenu:false,
    trackMenu:false,
}

const actions = { 
    getAlbums({commit}){
        //update to use AXIOS,
        return new Promise(()=>{
            var myHeaders = new Headers();
                myHeaders.append("Content-Type", "application/json");
                myHeaders.append("Accept", "application/json");
            var requestOptions = {
                method: 'GET',
                headers: myHeaders,
                redirect: 'follow'
                };
            fetch("http://127.0.0.1:8000/api/v1/albums", requestOptions)
            .then(response => response.text())
            .then(result =>{
                result = JSON.parse(result);
                commit('getAlbums', result.data);
            })
            .catch(error => console.log('error', error));
        })
    },
    addAlbums({commit},album){
        //update to use AXIOS,
        return new Promise(()=>{
            var myHeaders = new Headers();
            myHeaders.append("Content-Type", "application/json");
            myHeaders.append("Accept", "application/json");

            var raw = JSON.stringify(album);

            var requestOptions = {
              method: 'POST',
              headers: myHeaders,
              body: raw,
              redirect: 'follow'
            };

            fetch("http://127.0.0.1:8000/api/v1/albums", requestOptions)
              .then(response => response.text())
              .then(result => {
                  this.getAlbums();
              })
              .catch(error => console.log('error', error));
        })
    }
}

const mutations = {
    getAlbums(state,result){
        result = Object.values(result);

        state.albums = result;
    }
}
export default {
    state,
    actions,
    mutations,
  };
