const state = {
    albums:[],
    artist:{
        name:'Tião Carreiro',
    },

    search:'',
    albumMenu:false,
    trackMenu:false,
}

const actions = { 
    //ALBUMS
    getAlbums({commit}){
        //update to use AXIOS,
        return new Promise(()=>{
            var myHeaders = new Headers();
                myHeaders.append("Content-Type", "application/json");
                myHeaders.append("Accept", "application/json");
            var requestOptions = {
                method: 'GET',
                headers: myHeaders,
                redirect: 'follow'
                };
            fetch("http://127.0.0.1:8000/api/v1/albums", requestOptions)
            .then(response => response.text())
            .then(result =>{
                result = JSON.parse(result);
                commit('getAlbums', result.data);
            })
            .catch(error => console.log('error', error));
        })
    },
    addAlbums({commit},album){
        return new Promise(()=>{
            var myHeaders = new Headers();
            myHeaders.append("Content-Type", "application/json");
            myHeaders.append("Accept", "application/json");

            var raw = JSON.stringify(album);
            var requestOptions = {
              method: 'POST',
              headers: myHeaders,
              body: raw,
              redirect: 'follow'
            };

            fetch("http://127.0.0.1:8000/api/v1/albums", requestOptions)
              .then(response => response.text())
              .then(result=>{
                  result = JSON.parse(result);
                  album.id = result.data.id;
                commit('addAlbum',album);
              })
              .catch(error => console.log('error', error));
        })
    },
    delAlbums({commit},album){
        return new Promise(()=>{
            var myHeaders = new Headers();
            myHeaders.append("Content-Type", "application/json");
            myHeaders.append("Accept", "application/json");

            var requestOptions = {
              method: 'DELETE',
              headers: myHeaders,
              redirect: 'follow'
            };

            fetch("http://127.0.0.1:8000/api/v1/albums/"+album, requestOptions)
              .then(response => response.text())
              .then(() => {
                commit('delAlbum',album);
              })
              .catch(error => console.log('error', error));
        })
    },
    //TRACKS
    // addTrack({commit},track){
    //     return new Promise(()=>{

    //     })
    // },
}

const mutations = {
    //ALBUMS
    getAlbums(state,result){
        state.albums = [];
        result = Object.values(result);
        state.albums = result;
    },
    addAlbum(state,album){
       state.albums.push(album);
    },
    delAlbum(state,album){
        let exists = state.albums.findIndex(x => x.id === album);
        if(exists !== -1){
            state.albums.splice(exists,1)
        }else{
            console.log('impossivel remover o desconto')
        }
    }
    //TRACKS


}
export default {
    state,
    actions,
    mutations,
  };
